fields.contents:
.user-contents {
    font-family: 'Noticia Text', serif;
    font-weight: 400;
    font-size: 1.2em;
    line-height: 1.6em;
    /*text-align: justify;*/
    word-wrap: break-word;
}

#document-title {
    font-family: 'Source Sans Pro', sans-serif;
    font-size: 48px;
    font-weight: 700;
}

#metadata-subtitle {
    font-family: 'Source Sans Pro', sans-serif;
    font-size: 40px;
    font-weight: 900;
}

#metadata-abstract {
    margin-left: 50px;
    margin-right: 50px;
}

#document-metadata .metadata {
    margin-bottom: 20px;
}

.pagination-footnote:before,
.pagination-footnote > * > *:first-child:before {
    font-family: 'Noticia Text', serif;
    font-weight: 400;
    font-size: 9px;
    text-align: left;
    vertical-align: super;
    line-height: 0;
}

.pagination-footnote > * > *:first-child:before {
    margin-right: 5px;
}

.pagination-footnote > * > * {
    font-family: 'Noticia Text', serif;
    font-weight: 400;
    font-size: 10px;
}

.pagination-pagenumber {
    font-family: 'Source Sans Pro', sans-serif;
    font-size: 12px;
    font-weight: 700;
}

.pagination-footnotes {
    margin-top: 25px;
}

.user-contents h1,
.user-contents h2,
.user-contents h3,
.user-contents p,
.user-contents blockquote,
.user-contents code,
.user-contents ol,
.user-contents ul {
    margin-bottom: 35px;
}

.user-contents h1,
.user-contents h2,
.user-contents h3 {
    font-family: 'Source Sans Pro', sans-serif;
    font-weight: 700;
    line-height: 1.25em;
}

.user-contents h1  {
    font-size: 24px;
}

.user-contents h2 {
    font-size: 20px;
}

.user-contents h3 {
    font-size: 16px;
}

.user-contents p,
.user-contents ol li,
.user-contents ul li {
    font-size: 16px;
    line-height: 1.75em;
}

.user-contents b {
    font-weight: 700;
}

.user-contents i {
    font-style: italic;
}

.user-contents ul,
.user-contents ol {
    padding-left: 15px;
}
.user-contents ul {
    list-style: square;
}

.user-contents ol {
    list-style: decimal;
}

.user-contents blockquote {
    margin-left: 40px;
    margin-right: 40px;
    font-size: 13px;
    line-height: 1.8em;
}

.user-contents code {
    display: block;
    font-family: "Courier New", Courier, mono-space;
    font-size: 13px;
    line-height: 1.75em;
}

#document-bibliography p {
    font-size: 14px;
    margin-bottom: 18px;
}

.equation {
    font-size: 80%;
}

